import React, { useState, useContext, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import GeneralContext from "./GeneralContext";
import "./BuyActionWindow.css";

const SellActionWindow = ({ uid, currentPrice }) => {
  const [stockQuantity, setStockQuantity] = useState(1);
  const [totalPrice, setTotalPrice] = useState(currentPrice || 0);
  const { closeActionWindow } = useContext(GeneralContext);

  useEffect(() => {
    // whenever qty changes, auto-update total price
    setTotalPrice((stockQuantity * (currentPrice || 0)).toFixed(2));
  }, [stockQuantity, currentPrice]);

  const handleSellClick = async () => {
    try {
      await axios.post("http://localhost:3001/sellOrder", {
        name: uid,
        qty: stockQuantity,
        price: totalPrice,
      });
      closeActionWindow();
    } catch (err) {
      console.error("❌ Error while selling:", err);
    }
  };

  return (
    <div className="container" id="sell-window" draggable="true">
      <div className="regular-order">
        <div className="inputs">
          <fieldset>
            <legend>Qty.</legend>
            <input
              type="number"
              name="qty"
              id="qty"
              min="1"
              onChange={(e) => setStockQuantity(e.target.value)}
              value={stockQuantity}
            />
          </fieldset>
          <fieldset>
            <legend>Total Price</legend>
            <input
              type="number"
              name="price"
              id="price"
              step="0.05"
              value={totalPrice}
              readOnly
            />
          </fieldset>
        </div>
      </div>

      <div className="buttons">
        <span>Total Value ₹{totalPrice}</span>
        <div>
          <Link className="btn btn-blue" onClick={handleSellClick}>
            Sell
          </Link>
          <Link to="" className="btn btn-grey" onClick={closeActionWindow}>
            Cancel
          </Link>
        </div>
      </div>
    </div>
  );
};

export default SellActionWindow;
