import React from "react";
import heroImg from "assets/homeHero.png"; // adjust path if needed

function Hero() {
  return (
    <div className="w-full flex justify-center px-4 py-16">
      <div className="text-center max-w-3xl">

        {/* Hero Image */}
        <img
          src={heroImg}
          alt="Hero"
          className="w-full max-h-[450px] mx-auto object-contain"
        />

        {/* Heading */}
        <h1 className="mt-10 text-3xl md:text-4xl font-semibold text-gray-900">
          Invest in everything
        </h1>

        {/* Subtitle */}
        <p className="mt-3 text-gray-600 text-sm md:text-base px-4">
          Online platforms to invest in stocks, derivatives, mutual funds, ETFs,
          bonds, and more.
        </p>

        {/* Button */}
        <button
          className="mt-6 bg-[#387ED1] hover:bg-[#1c70d3] text-white 
                     font-semibold py-3 px-10 rounded-md text-lg transition-all"
        >
          Sign up for free
        </button>

      </div>
    </div>
  );
}

export default Hero;
