import React, { useState, useEffect } from "react";
import { TrendingUp, Shield, Zap, ArrowRight } from "lucide-react";

function Hero() {
  const [isVisible, setIsVisible] = useState(false);
  const [activeFeature, setActiveFeature] = useState(0);

  useEffect(() => {
    setIsVisible(true);
    const interval = setInterval(() => {
      setActiveFeature((prev) => (prev + 1) % 3);
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  const features = [
    { icon: TrendingUp, text: "Real-time Market Data", color: "text-emerald-400" },
    { icon: Shield, text: "Bank-Level Security", color: "text-blue-400" },
    { icon: Zap, text: "Lightning Fast Trades", color: "text-purple-400" }
  ];

  const investmentTypes = [
    "Stocks", "Derivatives", "Mutual Funds", "ETFs", "Bonds", "Crypto"
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-blue-900 to-slate-900 relative overflow-hidden">
      {/* Animated background elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute w-96 h-96 bg-blue-500 rounded-full mix-blend-multiply filter blur-3xl opacity-20 animate-blob top-0 -left-4"></div>
        <div className="absolute w-96 h-96 bg-purple-500 rounded-full mix-blend-multiply filter blur-3xl opacity-20 animate-blob animation-delay-2000 top-0 -right-4"></div>
        <div className="absolute w-96 h-96 bg-pink-500 rounded-full mix-blend-multiply filter blur-3xl opacity-20 animate-blob animation-delay-4000 bottom-0 left-1/2"></div>
      </div>

      {/* Grid pattern overlay */}
      <div className="absolute inset-0 bg-grid-pattern opacity-10"></div>

      <div className="container mx-auto px-4 py-20 relative z-10">
        <div className="max-w-6xl mx-auto">
          {/* Hero Image with floating animation */}
          <div className={`transform transition-all duration-1000 ${isVisible ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'}`}>
            <div className="relative group">
              <div className="absolute inset-0 bg-gradient-to-r from-blue-500 to-purple-600 rounded-2xl blur-2xl opacity-30 group-hover:opacity-50 transition-opacity duration-500"></div>
              <img
                src="https://images.unsplash.com/photo-1611974789855-9c2a0a7236a3?w=1200&h=600&fit=crop"
                alt="Investment platform dashboard"
                className="relative rounded-2xl shadow-2xl w-full h-auto object-cover transform group-hover:scale-105 transition-transform duration-700"
                style={{ maxHeight: "500px" }}
              />
              {/* Floating stats cards */}
              <div className="absolute -bottom-8 left-8 bg-white/10 backdrop-blur-lg rounded-xl p-4 shadow-xl border border-white/20 animate-float">
                <div className="text-emerald-400 text-2xl font-bold">+24.5%</div>
                <div className="text-white/80 text-sm">Portfolio Growth</div>
              </div>
              <div className="absolute -top-4 right-8 bg-white/10 backdrop-blur-lg rounded-xl p-4 shadow-xl border border-white/20 animate-float animation-delay-1000">
                <div className="text-blue-400 text-2xl font-bold">$2.4M</div>
                <div className="text-white/80 text-sm">Volume Today</div>
              </div>
            </div>
          </div>

          {/* Main content */}
          <div className={`text-center mt-20 transform transition-all duration-1000 delay-300 ${isVisible ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'}`}>
            <h1 className="text-6xl md:text-7xl font-bold text-white mb-6 leading-tight">
              Invest in{" "}
              <span className="bg-gradient-to-r from-blue-400 via-purple-400 to-pink-400 text-transparent bg-clip-text animate-gradient">
                everything
              </span>
            </h1>
            
            <p className="text-xl text-gray-300 mb-8 max-w-3xl mx-auto leading-relaxed">
              Your all-in-one platform to invest in stocks, derivatives, mutual funds, ETFs, bonds, and more—all from one seamless interface
            </p>

            {/* Investment types pills */}
            <div className="flex flex-wrap justify-center gap-3 mb-10">
              {investmentTypes.map((type, index) => (
                <span
                  key={type}
                  className="px-4 py-2 bg-white/10 backdrop-blur-sm rounded-full text-sm text-white border border-white/20 hover:bg-white/20 transition-all duration-300 hover:scale-110 cursor-pointer"
                  style={{ animationDelay: `${index * 100}ms` }}
                >
                  {type}
                </span>
              ))}
            </div>

            {/* CTA Button */}
            <button className="group relative px-8 py-4 bg-gradient-to-r from-blue-500 to-purple-600 text-white font-semibold text-lg rounded-full shadow-lg hover:shadow-2xl transform hover:scale-105 transition-all duration-300 overflow-hidden">
              <span className="relative z-10 flex items-center gap-2">
                Sign up for free
                <ArrowRight className="w-5 h-5 group-hover:translate-x-1 transition-transform" />
              </span>
              <div className="absolute inset-0 bg-gradient-to-r from-purple-600 to-pink-600 opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
            </button>

            <p className="text-gray-400 text-sm mt-4">No credit card required • Start in 2 minutes</p>
          </div>

          {/* Feature highlights */}
          <div className={`grid md:grid-cols-3 gap-6 mt-20 transform transition-all duration-1000 delay-500 ${isVisible ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'}`}>
            {features.map((feature, index) => {
              const Icon = feature.icon;
              return (
                <div
                  key={index}
                  className={`bg-white/5 backdrop-blur-sm rounded-xl p-6 border border-white/10 hover:bg-white/10 transition-all duration-300 hover:scale-105 cursor-pointer ${
                    activeFeature === index ? 'ring-2 ring-blue-400' : ''
                  }`}
                >
                  <Icon className={`w-8 h-8 ${feature.color} mb-3`} />
                  <h3 className="text-white font-semibold text-lg">{feature.text}</h3>
                </div>
              );
            })}
          </div>
        </div>
      </div>

      <style jsx>{`
        @keyframes blob {
          0%, 100% { transform: translate(0, 0) scale(1); }
          33% { transform: translate(30px, -50px) scale(1.1); }
          66% { transform: translate(-20px, 20px) scale(0.9); }
        }
        @keyframes float {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-20px); }
        }
        @keyframes gradient {
          0%, 100% { background-position: 0% 50%; }
          50% { background-position: 100% 50%; }
        }
        .animate-blob { animation: blob 7s infinite; }
        .animation-delay-1000 { animation-delay: 1s; }
        .animation-delay-2000 { animation-delay: 2s; }
        .animation-delay-4000 { animation-delay: 4s; }
        .animate-float { animation: float 3s ease-in-out infinite; }
        .animate-gradient { 
          background-size: 200% 200%;
          animation: gradient 3s ease infinite;
        }
        .bg-grid-pattern {
          background-image: 
            linear-gradient(rgba(255,255,255,0.05) 1px, transparent 1px),
            linear-gradient(90deg, rgba(255,255,255,0.05) 1px, transparent 1px);
          background-size: 50px 50px;
        }
      `}</style>
    </div>
  );
}

export default Hero;
