import React from "react";
import "./Hero.css"; // <-- Add a small custom CSS file

function Hero() {
  return (
    <div className="hero-container">
      <div className="hero-content">
        <img
          src="assets/homeHero.png"
          alt="Hero"
          className="hero-image"
        />

        <h1 className="hero-title">Invest in everything</h1>

        <p className="hero-subtitle">
          Online platforms to invest in stocks, derivatives, mutual funds, ETFs,
          bonds, and more.
        </p>

        <button className="hero-btn">Sign up for free</button>
      </div>
    </div>
  );
}

export default Hero;
