import React from "react";

function Hero() {
  return (
    <div className="container p-5 home-hero mt-5">
      <div className="row text-center">
        <img
          src="/assets/homeHero.png"
          alt="Investment platform hero banner showing trading interface"
          className="img-fluid"
          style={{ maxHeight: "500px", objectFit: "contain" }}
        />

        <h1 className="mt-5 fs-2">Invest in everything</h1>
        <p className="fs-6 p-2">
          Online platform to invest in stocks, derivatives, mutual funds, ETFs, bonds, and more
        </p>
        <button 
          className="btn btn-primary p-2 fs-5 mx-auto fw-semibold" 
          style={{ 
            maxWidth: "300px", 
            width: "90%",
            backgroundColor: '#387ED1'
          }}
        >
          Sign up for free
        </button>
      </div>
    </div>
  );
}

export default Hero;
